   124â†’pub fn handle_terrain_query_events(
   125â†’    mut query_events: EventReader<TerrainQueryEvent>,
   126â†’    mut response_events: EventWriter<TerrainQueryResponse>,
   127â†’    chunk_manager: Res<ChunkManager>,
   128â†’    chunks_query: Query<&Chunk>,
   129â†’    _world_generator: Res<WorldGenerator>,
   130â†’) {
   131â†’    for event in query_events.read() {
   132â†’        match event.radius {
   133â†’            Some(radius) => {
   134â†’                let terrain = TerrainQuerySystem::get_terrain_in_radius(
   135â†’                    &chunk_manager,
   136â†’                    event.position.0,
   137â†’                    event.position.1,
   138â†’                    radius,
   139â†’                    &chunks_query,
   140â†’                );
   141â†’
   142â†’                let analysis = TerrainQuerySystem::analyze_area(
   143â†’                    &chunk_manager,
   144â†’                    event.position.0,
   145â†’                    event.position.1,
   146â†’                    radius,
   147â†’                    &chunks_query,
   148â†’                );
   149â†’
   150â†’                response_events.write(TerrainQueryResponse {
   151â†’                    terrain,
   152â†’                    analysis: Some(analysis),
   153â†’                });
   154â†’            }
   155â†’            None => {
   156â†’                if let Some(terrain) = TerrainQuerySystem::get_terrain_at_position(
   157â†’                    &chunk_manager,
   158â†’                    event.position.0,
   159â†’                    event.position.1,
   160â†’                    &chunks_query,
   161â†’                ) {
   162â†’                    response_events.write(TerrainQueryResponse {
   163â†’                        terrain: vec![terrain],
   164â†’                        analysis: None,
   165â†’                    });
   166â†’                }
   167â†’            }
   168â†’        }
   169â†’    }
   170â†’}
