   172â†’pub fn handle_pathfinding_events(
   173â†’    mut path_events: EventReader<PathfindingRequestEvent>,
   174â†’    mut response_events: EventWriter<PathfindingResponseEvent>,
   175â†’    chunk_manager: Res<ChunkManager>,
   176â†’    chunks_query: Query<&Chunk>,
   177â†’) {
   178â†’    for event in path_events.read() {
   179â†’        let path =
   180â†’            TerrainQuerySystem::find_path(&chunk_manager, event.start, event.goal, &chunks_query);
   181â†’
   182â†’        let cost = path.as_ref().map(|p| {
   183â†’            if p.len() < 2 {
   184â†’                0.0
   185â†’            } else {
   186â†’                let mut total_cost = 0.0;
   187â†’                for i in 1..p.len() {
   188â†’                    let dx = p[i].0 - p[i - 1].0;
   189â†’                    let dy = p[i].1 - p[i - 1].1;
   190â†’                    total_cost += ((dx * dx + dy * dy) as f32).sqrt();
   191â†’                }
   192â†’                total_cost
   193â†’            }
   194â†’        });
   195â†’
   196â†’        response_events.write(PathfindingResponseEvent { path, cost });
   197â†’    }
   198â†’}
