    77â†’fn generate_world(file_name: &str, map_name: &str, seed: u64) -> Result<(), Box<dyn std::error::Error>> {
    78â†’    // Create world generator with specified seed
    79â†’    let mut world_generator = WorldGenerator::new(WorldConfig::default());
    80â†’    world_generator.set_seed(seed);
    81â†’
    82â†’    println!("ğŸŒ Generating terrain chunks...");
    83â†’
    84â†’    // Generate chunks around center for saving
    85â†’    let center_x = 0;
    86â†’    let center_y = 0;
    87â†’    let radius = 12; // Save 25x25 chunk area around center (400x400 tiles)
    88â†’
    89â†’    let total_chunks = ((radius * 2 + 1) * (radius * 2 + 1)) as usize;
    90â†’    let mut generated_chunks = 0;
    91â†’
    92â†’    // Multi-layer chunks: terrain + resources
    93â†’    let mut multi_layer_chunks = HashMap::new();
    94â†’
    95â†’    for chunk_x in (center_x - radius)..=(center_x + radius) {
    96â†’        for chunk_y in (center_y - radius)..=(center_y + radius) {
    97â†’            // Generate terrain layer
    98â†’            let terrain_layer = world_generator.generate_procedural_chunk(chunk_x, chunk_y);
